import React, {Component} from 'react';
import axios from "axios";

const Context = React.createContext(undefined);

export class Provider extends Component {
    state = {
        items:[]
    }

    componentDidMount() {
        axios.get(`http://localhost:3452/item`).then(res => {
            console.log(res.data)
            this.setState({items:res.data})
        }).catch(err => console.log(err));

        localStorage.setItem('username', uuidv4());

        console.log(localStorage.getItem('username'));
        axios.post(`http://localhost:3252/cart/createCart/${localStorage.getItem('username')}`).then(res => {
            console.log(res.data)
            this.setState({items:res.data})
        }).catch(err => console.log(err));
    }

    render() {
        return (
            <Context.Provider value={this.state}>
                {this.props.children}
            </Context.Provider>
        );
    }
}

function uuidv4() {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
        var r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
        return v.toString(16);
    });}

export const Comsumer = Context.Consumer;
