import React, {Component, createRef} from 'react';
import {Link, Redirect} from "react-router-dom";
import {Comsumer} from "../context";
import axios from "axios";

class ItemEdit extends Component {
    state = {
        title: '',
        description: '',
        price: ''
    }

    addItem = () => {
        axios({
            method: 'post',
            url: `http://localhost:3452/item`,
            data: {
                "title": this.title.value,
                "description": this.description.value,
                "unitPrice": this.price.value,
                "quantity": 1
            }
        }).then().catch(err => console.log(err));
    }


    onTitleChange = (e) => {
        this.setState({title:e.target.value})
    }

    onDescriptionChange = (e) => {
        this.setState({description:e.target.value})
    }

    onPriceChange = (e) => {
        this.setState({price:e.target.value})
    }

    render() {
        return (
            <Comsumer>
                {value => {
                    return (
                        <div className="mx-5">
                            <h1>Create Item</h1>
                            <hr/>
                            <br/>
                            <form>
                                <div className="form-group">
                                    <label htmlFor="title">Title</label>
                                    <input type="text" className="form-control" id="title" name='title'
                                           placeholder="Title" required value={this.state.title} onChange={this.onTitleChange}/>
                                </div>
                                <div className="form-group">
                                    <label htmlFor="description">Description</label>
                                    <textarea className="form-control" id="description" name='description' rows="3" required value={this.state.description} onChange={this.onDescriptionChange}></textarea>
                                </div>
                                <div className="form-group">
                                    <label htmlFor="price">Price</label>
                                    <input type="number" className="form-control" id="price" name='price'
                                           placeholder="Price" min="0.01" step="0.01" required value={this.state.price} onChange={this.onPriceChange}/>
                                </div>
                                <hr/>
                                <button className="btn btn-lg btn-dark w-100" onClick={this.addItem}>Create Item
                                </button>
                            </form>
                        </div>
                    )
                }}
            </Comsumer>
        );
    }

}

export default ItemEdit;
