import React, {Component, useReducer} from 'react';
import axios from "axios";

const Context = React.createContext(undefined);

export class Provider extends Component {
    state = {
        items:[],
        isLoaded: false,
        dispatch: action => this.setState(state => useReducer(state, action))
    }

    deleteItem = (id) => {
        axios.delete(`http://localhost:3452/item/${id}`).then(res => {
            this.setState({items:[...this.state.items.filter(item => item.id != id)]})
        }).catch(err => console.log(err));
    }

    componentDidMount() {
        axios.get(`http://localhost:3452/item`).then(res => {
            this.setState({items:res.data, isLoaded: true})
        }).catch(err => {
            console.log(typeof err)

            if(err instanceof Error) {
                this.setState({isLoaded: true})
            }
        });
    }

    render() {
        return (
            <Context.Provider value={{state: this.state, deleteItem: this.deleteItem, addItem: this.addItem}}>
                {this.props.children}
            </Context.Provider>
        );
    }
}



export const Comsumer = Context.Consumer;
