import React, {Component} from 'react';
import {Link} from "react-router-dom";

class CartItem extends Component {
    render() {
        const { id, title , unitPrice } = this.props.item;
        return (
            <div className='col-md-11'>
                <div className="card mb-4 shadow-sm">
                    <div className="card-body">
                        <h3><strong>{title}</strong></h3>
                        <p className="card-text">${unitPrice}</p>
                        {/*<button className="btn btn-dark m-1" onClick={this.addToCart.bind(this, this.props.item)}>Add to cart</button>*/}
                        <div className="float-right">
                            <Link to={`/edit-item/${id}`} className="btn btn-info m-1">Edit item</Link>
                            <button className="btn btn-danger m-1" onClick={this.props.removeItem.bind(this, id)}>Remove item</button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default CartItem;
