import React, {Component} from 'react';
import {Link} from "react-router-dom";
import axios from "axios";

class ItemEdit extends Component {
    addItem = (e) => {
        let item = e.target.value;
        console.log(e);
        // axios({
        //     method: 'post',
        //     url: `http://localhost:3452/item`,
        //     data: {
        //         "title": item.title,
        //         "description": item.description,
        //         "unitPrice": item.price,
        //         "quantity": 1
        //     }
        // }).catch(err => console.log(err));
    }

    render() {
        return (
            <div className="mx-5">
                <h1>Create Item</h1>
                <hr />
                <br />
                <form>
                    <div className="form-group">
                        <label htmlFor="title">Title</label>
                        <input type="text" className="form-control" id="title"
                               placeholder="Title" required/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="description">Description</label>
                        <textarea className="form-control" id="description" rows="3" required></textarea>
                    </div>
                    <div className="form-group">
                        <label htmlFor="price">Price</label>
                        <input type="number" className="form-control" id="price"
                               placeholder="Price" min="0.01" step="0.01" required/>
                    </div>
                    <hr />
                    <Link to="/" className="btn btn-lg btn-dark w-100" onClick={(e) => this.addItem(e)}>Create Item</Link>
                </form>
            </div>
        );
    }
}

export default ItemEdit;
