import React, {Component, createRef} from 'react';
import {Link, Redirect} from "react-router-dom";
import axios from "axios";

class ItemEdit extends Component {
    state = {
        redirect: false
    }

    addItem = () => {
        axios({
            method: 'post',
            url: `http://localhost:3452/item`,
            data: {
                "title": this.title.value,
                "description": this.description.value,
                "unitPrice": this.price.value,
                "quantity": 1
            }
        }).then(res => this.setState({redirect: true})).catch(err => console.log(err));
    }

    render() {
        if(this.state.redirect) {
            return <Redirect to="/" />
        } else {
            return (
                <div className="mx-5">
                    <h1>Create Item</h1>
                    <hr />
                    <br />
                    <form>
                        <div className="form-group">
                            <label htmlFor="title">Title</label>
                            <input type="text" className="form-control" id="title"
                                   placeholder="Title" required ref={ref => this.title = ref}/>
                        </div>
                        <div className="form-group">
                            <label htmlFor="description">Description</label>
                            <textarea className="form-control" id="description" rows="3" required ref={ref => this.description = ref}></textarea>
                        </div>
                        <div className="form-group">
                            <label htmlFor="price">Price</label>
                            <input type="number" className="form-control" id="price"
                                   placeholder="Price" min="0.01" step="0.01" required ref={ref => this.price = ref}/>
                        </div>
                        <hr />
                        <Link to="/" className="btn btn-lg btn-dark w-100" onClick={this.addItem}>Create Item</Link>
                    </form>
                </div>
            );
        }

    }
}

export default ItemEdit;
