import React, {Component} from 'react';

const Context = React.createContext(undefined);

export class Provider extends Component {
    state = {
        items: []
    }

    render() {
        return (
            <Context.Provider value={this.state}>
                {this.props.children}
            </Context.Provider>
        );
    }
}

export const Comsumer = Context.Consumer;
