import React, {Component, createRef} from 'react';
import {Link} from "react-router-dom";
import {Comsumer} from "../context";

class ItemEdit extends Component {
    constructor(props) {
        super(props);

        this.title = createRef();
        this.description = createRef();
        this.price = createRef();
    }

    render() {
        return (
            <Comsumer>
                {value => {
                    return (
                        <div className="mx-5">
                            <h1>Create Item</h1>
                            <hr />
                            <br />
                            <form>
                                <div className="form-group">
                                    <label htmlFor="title">Title</label>
                                    <input type="text" className="form-control" id="title"
                                           placeholder="Title" required ref={this.title}/>
                                </div>
                                <div className="form-group">
                                    <label htmlFor="description">Description</label>
                                    <textarea className="form-control" id="description" rows="3" required ref={this.description}></textarea>
                                </div>
                                <div className="form-group">
                                    <label htmlFor="price">Price</label>
                                    <input type="number" className="form-control" id="price"
                                           placeholder="Price" min="0.01" step="0.01" required ref={this.price}/>
                                </div>
                                <hr />
                                <Link to="/" className="btn btn-lg btn-dark w-100" onClick={value.addItem.bind(this, this.title.value, this.description.value, this.price.value)}>Create Item</Link>
                            </form>
                        </div>
                    )}}
            </Comsumer>
        );
    }
}

export default ItemEdit;
