import React, {Component} from 'react';
import axios from 'axios';

import PropTypes from 'prop-types';

class Item extends Component {
    addToCart = (id) => {
        axios.delete(`http://localhost:3452/item/${id}`).then(res => {
            this.setState({items:res.data})
        }).catch(err => console.log(err));
        window.location.reload()

    }

    render() {
        const { id, title, description, unitPrice } = this.props.item;
        return (
            <div className='col-md-3'>
                <div className="card mb-4 shadow-sm">
                    <div className="card-body">
                        <h3><strong>{title}</strong></h3>
                        <p className="card-text">{description}</p>
                        <p className="card-text">${unitPrice}</p>
                        <button className="btn btn-dark m-1" onClick={this.addToCart.bind(this, id)}>Add to cart</button>
                        <div className="float-right">
                            <button className="btn btn-dark m-1" onClick={this.addToCart.bind(this, id)}>Edit item</button>
                            <button className="btn btn-dark m-1" onClick={this.addToCart.bind(this, id)}>Delete item</button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

Item.propTypes = {
    item: PropTypes.object.isRequired
}

const itemStyle = {
    backgroundColor: '#f4f4f4',
    padding: '10px',
    borderBottom: '1px #ccc dotted'
}

export default Item;
