import React, {Component} from 'react';
import axios from 'axios';
import CartItem from "./CartItem";
import Spinner from './spinner';

class Cart extends Component {
    state = {
        items: {},
        total: {},
        isLoaded: false
    }

    removeItem = (id) => {
        axios.delete(`http://localhost:3252/cart/removeItem/${id}/${localStorage.getItem('username')}`).then(res => {
            this.setState({items: [...this.state.items.filter(item => item.id !== id)]})
        }).catch(err => console.log(err));
    }

    changeQuantity = (event, id) => {
        console.log(event);
        console.log(id);
        let quantity = event.target.value;
        event.target.value = quantity;
        axios.patch(`http://localhost:3252/cart/changeQuantity/${id}/${quantity}/${localStorage.getItem('username')}`).then(res => {
            // this.setState({items: [...this.state.items.filter(item => item.id != id)]})
        }).catch(err => console.log(err));
    }

    componentDidMount() {
        axios.get(`http://localhost:3252/cart/showCart/${localStorage.getItem('username')}`)
            .then(res => {
                this.setState({items: res.data.items, total: res.data.total, isLoaded: true})
            }).catch(err => console.log(err));
    }

    render() {
        const {items, isLoaded} = this.state;
        if (!isLoaded) {
            return <Spinner/>
        } else if (items === undefined || items.length === 0) {
            return (
                <p>no items in cart</p>
            )
        } else {
            return (
                <React.Fragment>
                    <div className="row mx-1">
                        <div className="col-10 row">
                            {this.state.items.map(item => (
                                <CartItem key={item.id} item={item} removeItem={this.removeItem} changeQuantity={this.changeQuantity}/>
                            ))}
                        </div>
                        <div className="col-2">
                            <p>Checkout</p>
                        </div>
                    </div>
                </React.Fragment>
            )
        }
    }
}

export default Cart;
